"use strict";

// Invoke plugins for certain actions.

var _require = require("./index"),
    store = _require.store;

var apiRunnerNode = require("../utils/api-runner-node");

store.subscribe(function () {
  var state = store.getState();
  // console.log("last action", state.lastAction.type)
  if (state.lastAction.type === "CREATE_NODE" && state.lastAction.payload.logical !== true) {
    var node = state.nodes[state.lastAction.payload.id];
    apiRunnerNode(`onNodeCreate`, { node });
  }
});